import { createSlice } from "@reduxjs/toolkit";

const addToCartReducer = createSlice({
  name: "addToCartReducer",
  initialState: {
    cartItem: [
      {
        title: "Test",
        price: 6,
        description: "This is a first product - amazing!",
        quantity: 1,
        total: 0,
      },
      {
        title: "Car",
        price: 700,
        description: "This is a first in the world",
        quantity: 1,
        total: 0,
      },
    ],
    myCart: [],
  },
  reducers: {
    addToCart: (state, action) => {
      const existingItem = state.myCart.find(
        (item) => item.title === action.payload.title
      );
      if (existingItem) {
        existingItem.quantity += action.payload.quantity;
        existingItem.total += existingItem.price * action.payload.quantity; // Fix calculation
      } else {
        state.myCart.push(action.payload);
      }
    },
    removecart: (state, action) => {
      const existingItemIndex = state.myCart.findIndex(
        (item) => item.title === action.payload.title
      );
      if (existingItemIndex !== -1) {
        const existingItem = state.myCart[existingItemIndex];
        if (existingItem.quantity === 1) {
          state.myCart.splice(existingItemIndex, 1); // Remove item from array
        } else {
          existingItem.quantity--;
          existingItem.total -= existingItem.price;
        }
      }
    },
  },
});

export const addToCartReducerActions = addToCartReducer.actions;
export default addToCartReducer.reducer;
