import { createSlice } from "@reduxjs/toolkit";

const addToCartReducer = createSlice({
  name: "addToCartReducer",
  initialState: {
    cartItem: [
      {
        title: "Test",
        price: 6,
        description: "This is a first product - amazing!",
        quantity: 1,
        total: 0,
      },
      {
        title: "Car",
        price: 700,
        description: "This is a first in the world",
        quantity: 1,
        total: 0,
      },
    ],
    myCart: {
      items: [],
      total: 0,
    },
  },
  reducers: {
    addToCart: (state, action) => {
      const existingItem = state.myCart.items.find(
        (item) => item.title === action.payload.title
      );
      if (existingItem) {
        existingItem.quantity += action.payload.quantity;
        existingItem.total += action.payload.quantity * action.payload.price;
      } else {
        const newItem = {
          ...action.payload,
          total: action.payload.quantity * action.payload.price,
        };
        state.myCart.items.push(newItem);
      }
      state.myCart.total = state.myCart.items.reduce(
        (total, item) => total + item.total,
        0
      );
    },
  },
});

export const addToCartReducerActions = addToCartReducer.actions;
export default addToCartReducer;
