import { useDispatch, useSelector } from "react-redux";
import classes from "./CartButton.module.css";
import { cardSlicerActions } from "../UI/cardSlicer";
import { addToCartReducerActions } from "./addToCartReducer";

const CartButton = () => {
  const dispatch = useDispatch();
  const countselector = useSelector(
    (state) => state.addToCart.addToCartReducerActions
  );
  console.log(countselector);

  const cartButtonHandler = () => {
    dispatch(cardSlicerActions.toggleCardVisibility());
  };
  return (
    <button className={classes.button}>
      <span onClick={cartButtonHandler}>My Cart</span>
      <span className={classes.badge}>1</span>
    </button>
  );
};

export default CartButton;
